document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".button-toggle-accordion").forEach((t=>{t.addEventListener("click",(function(t){const e=t.currentTarget.parentElement,c=e.querySelector(".accordion-text");c&&c.classList.toggle("active"),e.classList.toggle("active");const o=e.querySelector(".button-toggle-accordion");o&&o.classList.toggle("active")}))})),document.querySelectorAll(".accordion-title").forEach((t=>{t.addEventListener("click",(function(t){const e=t.currentTarget.parentElement,c=e.querySelector(".accordion-text");c&&c.classList.toggle("active");const o=e.querySelector(".button-toggle-accordion");o&&o.classList.toggle("active"),e.classList.toggle("active")}))}))}));